    protected enum BlockType {
        NONE(0),
        GOLD(-1),
        GRAY(2),
        RUBY(1),
        YLLW(1),
        BLUE(1),
        MGNT(1),
        LIME(1),
        WHIT(1),
        ORNG(1),
        CYAN(1);

        protected final int maxHits;

        BlockType(final int maxHits) {
            this.maxHits = maxHits;
        }
    }

    public newLevel(int levelNumber) {
        this.levelNumber = levelNumber;
        this.layout = generateLayout(levelNumber);
        this.name = "Level " + levelNumber;
    }

